mapper.update:
UPDATE member
SET use_at = 'Y'
WHERE mbs_id =#{mbsId}

UPDATE member
SET mbs_pw = #{mbsPw}
, mbs_mail =
#{mbsMail}
, mbs_addr = #{mbsAddr}
, mbs_ph = #{mbsPh}
,mbs_nm= #{mbsNm}
WHERE mbs_id = #{mbsId}

mapper.delete:
delete from member where
MBS_id = #{mbsId}
and MBS_PW = #{mbsPw}

mapper.select:
select count(*) as cnt
from member
where mbs_id =#{memId}
